import React from "react";

const Rating = ({ value, text }) => {
  return (
    <div className="rating">
      <span>
        // 1 star
        <i
          className={
            value >= 1
              ? "fas fa-star"
              : value >= "0.5"
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        ></i>
        // 2 stars
        <i
          className={
            value >= 2
              ? "fas fa-star"
              : value >= "1.5"
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        ></i>
        // 3 stars
        <i
          className={
            value >= 3
              ? "fas fa-star"
              : value >= "2.5"
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        ></i>
        // 4 stars
        <i
          className={
            value >= 4
              ? "fas fa-star"
              : value >= "3.5"
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        ></i>
        // 5 stars
        <i
          className={
            value >= 5
              ? "fas fa-star"
              : value >= "4.5"
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        ></i>
      </span>
      <span>{text && text}</span>
    </div>
  );
};

export default Rating;
